contracts/BankRecycle.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";

contract BankRecycle is ERC1155 {
    // Struktur untuk menyimpan informasi produk
    struct Product {
        uint id;
        string namaMaterial;
        uint256 beratMaterial;
        uint hargaMaterial;
        address owner;
        bool purchased;
    }

    // Mapping untuk menyimpan data produk berdasarkan ID produk
    mapping(uint => Product) public products;

    // Counter untuk melacak total produk yang telah dibuat
    uint public totalProduct = 0;

    constructor() ERC1155("https://silver-musical-yak-924.mypinata.cloud/ipfs/QmbEv5pBGK4bR8BU1F48H9cG5mT8pQqwUtnWWFhCz5Z4yn/{id}.json") {}

    // Fungsi untuk membuat produk baru
    function createProduct(
        string memory _namaMaterial,
        uint256 _beratMaterial,
        uint _hargaMaterial
    ) public {
        // Increment counter produk
        totalProduct++;

        // Mencetak token baru ke pengguna
        _mint(msg.sender, totalProduct, 1, "");

        // Merekam informasi produk yang dibuat
        products[totalProduct] = Product(
            totalProduct,
            _namaMaterial,
            _beratMaterial,
            _hargaMaterial,
            msg.sender,
            false
        );

        // Emit event ketika produk dibuat
        emit ProductCreated(
            totalProduct,
            _namaMaterial,
            _beratMaterial,
            _hargaMaterial,
            msg.sender,
            false
        );
    }

    // Fungsi untuk membeli produk
    function purchaseProduct(uint _productId) public {
        // Mendapatkan referensi produk berdasarkan ID
        Product storage product = products[_productId];

        // Validasi produk yang ada
        require(product.id > 0 && product.id <= totalProduct, "Product ID tidak ada");
        require(!product.purchased, "Product sudah dibeli");
        require(msg.sender != product.owner, "Tidak dapat membeli produk Anda sendiri");

        // Melakukan transfer token ke pembeli
        safeTransferFrom(product.owner, msg.sender, _productId, 1, "");

        // Mengupdate informasi produk setelah pembelian
        product.owner = msg.sender;
        product.purchased = true;

        // Emit event ketika produk dibeli
        emit ProductPurchased(_productId, msg.sender);
    }

    // Fungsi untuk transfer token ke alamat lain tanpa memerlukan saldo khusus
    function transferToken(address _to, uint256 _tokenId) public {
    safeTransferFrom(msg.sender, _to, _tokenId, 1, "");

        // Mengupdate alamat pemilik pada data produk setelah transfer
        Product storage product = products[_tokenId];
        product.owner = _to; // Memperbarui alamat pemilik produk ke alamat baru

        emit TokenTransferred(_tokenId, msg.sender, _to, 1);
    }


    // Fungsi untuk mendapatkan nama material dari token ID
    function getName(uint256 _tokenId) public view returns (string memory) {
        Product storage product = products[_tokenId];
        return product.namaMaterial;
    }

    // Fungsi untuk mendapatkan detail dari token ID
    function getDetails(uint256 _tokenId) public view returns (
        string memory,
        uint256,
        uint
    ) {
        Product storage product = products[_tokenId];
        return (
            product.namaMaterial,
            product.beratMaterial,
            product.hargaMaterial
        );
    }

    // Event yang diemitter ketika produk berhasil dibuat
    event ProductCreated(
        uint id,
        string namaMaterial,
        uint256 beratMaterial,
        uint hargaMaterial,
        address owner,
        bool purchased
    );

    // Event yang diemitter ketika produk berhasil dibeli
    event ProductPurchased(uint id, address purchaser);

    // Event yang diemitter ketika token berhasil dikirim
    event TokenTransferred(uint256 tokenId, address from, address to, uint256 amount);
}

contracts/MaterialContract.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.20;

contract MaterialContract {
	 struct Material {
		  string fullName;
		  string city;
		  string email;
		  string materialName;
		  uint256 weight;
		  uint256 price;
		  address userAddress;
	 }

	 mapping(uint256 => Material) public materials;
	 uint256 public materialsCount;

	 event MaterialAdded(uint256 indexed id, address indexed userAddress);

	 function addMaterial(
		  string memory _fullName,
		  string memory _city,
		  string memory _email,
		  string memory _materialName,
		  uint256 _weight,
		  uint256 _price
	 ) external {
		  materials[materialsCount] = Material({
				fullName: _fullName,
				city: _city,
				email: _email,
				materialName: _materialName,
				weight: _weight,
				price: _price,
				userAddress: msg.sender
		  });
		  emit MaterialAdded(materialsCount, msg.sender);
		  materialsCount++;
	 }

	 function getMaterial(uint256 _id)
		  external
		  view
		  returns (
				string memory,
				string memory,
				string memory,
				string memory,
				uint256,
				uint256,
				address
		  )
	 {
		  Material storage material = materials[_id];
		  return (
				material.fullName,
				material.city,
				material.email,
				material.materialName,
				material.weight,
				material.price,
				material.userAddress
		  );
	 }
}

